import React from 'react'

export default function Form() {

 
    const handleUpCase=()=>{
        console.log("Handled");
    }
    const handelChange=()=>{
        console.log("Text input detected");
    }


  return (
    <div className='container' >
    <h1 className='text-center text-info' >Text Converter</h1>
    <div className="mb-3">
    <textarea className="form-control" onChange={handelChange} id="myForm" rows="5"></textarea>
    <button className='btn btn-outline-info m-2' onClick={handleUpCase} >Convert to UpperCase</button>
    <button className='btn btn-outline-info m-2' >Convert to LowerCase</button>
    </div>
    </div>
  )
}
