         </Switch>
        <Switch> 
         <Route exact path="/about" ><About accBg={accBg} textCol={textCol} /></Route> 
       </Switch> 
       <Switch>
        <Route path="/Linkedaccordina">
        <LinkedAccordina accBg={accBg} textCol={textCol} />
        </Route>
       </Switch>
     </>
     </Router> 
  );
}

export default App;
