import { useState } from 'react';
import './App.css';
// import Accordian from './components/Accordian';
import Form from './components/Form';
import Navbar from './components/Navbar';
import LinkedAccordina from './components/LinkedAccordina';

function App() {


  const [mood,setMood]=useState("light")
  const [textCol,setTextCol]=useState('dark')
  const [accBg,setAccBg]=useState(null);

  var newAccbg=(bg,col)=>{
    setAccBg={
      backgroundColor:bg,
      color:col,
    }
  }


  const toggleMood=()=>{
    if(mood==='light'){
      setMood("dark");
      setTextCol("light");
      newAccbg("dark","light");
      document.body.style.backgroundColor="#000";
      document.body.style.color="#fff";
    }
    else{
      setMood("light");
      setTextCol("dark");
      setAccBg();
      document.body.style.backgroundColor="#fff";
      document.body.style.color="#000";
    }
  }

  return (
    <>
    <Navbar mood={mood} toggleMood={toggleMood} textCol={textCol} />
    <Form/>
    <LinkedAccordina accBg={accBg} />
    {/* <Accordian mood={mood} /> */}
    </>
  );
}

export default App;
